import './index.css';


function Line(props) {
  return (
    <line
      className="line"
      x1={props.x1}
      y1={props.y1}
      x2={props.x2}
      y2={props.y2}
      style="stroke:rgb(255,0,0);stroke-width:2" />
  )
}

class Canvas extends React.Component {

  renderLine(i, x1, y1, x2, y2) {
    return (
      <Line
        key={i}
        x1={x1}
        y1={y1}
        x2={x2}
        y2={y2}
      />
    );
  }

  render() {
    return (
      <div>
        <svg height="500" width="500">
          {[
            this.renderLine(0, 0, 0, 200, 200),
            this.renderLine(1, 0, 0, 100, 200),
            this.renderLine(2, 0, 0, 5, 20),
