import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Line(props) {
  return (
    <line
      className="line"
      x1={props.x1}
      y1={props.y1}
      x2={props.x2}
      y2={props.y2}
      style={{display: props.show? "" : "none"}}
      />
  )
}

class Canvas extends React.Component {

  renderLine(i, x1, y1, x2, y2, show) {
    return (
      <Line
        key={i}
        x1={x1}
        y1={y1}
        x2={x2}
        y2={y2}
        show={show}
      />
    );
  }

  renderLines = (lines) =>
      lines.map((line, i) => {
        return this.renderLine(i, line.x1, line.y1, line.x2, line.y2, line.show)
      }
    );

  render() {
    return (
      <div>
        <svg height="500" width="500">
          {this.renderLines(this.props.lines)}
        </svg>
      </div>
    );
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);
    const points = [
      {
        x: 0,
        y: 0,
      },
      {
        x: 50,
        y: 50,
      },
      {
        x: 40,
        y: 170,
      },
    ];
    this.state = {
      points: points,
      play: false,
      lines: this.generateLines(points),
      playlist: []
    };
  }

  generateLines(points) {

    const lines = [];

    for (let i = 0; i < points.length-1; i++) {
      lines.push({
        x1: points[i].x,
        y1: points[i].y,
        x2: points[i+1].x,
        y2: points[i+1].y,
        show: false,
      })
    }

    return lines;
  }

  showLine(i) {
    const lines = this.state.lines.slice();
    lines[i].show = true;
    this.setState({
      lines: lines
    })
  }

  showLines() {
    for (let i=0; i<this.state.lines.length; i++) {
      this.state.playlist.push(
        setTimeout(() => {
          this.showLine(i);
        }, 1000*i)
      );
    }
  }

  clearLines() {
    for (let i=0; i<this.state.lines.length; i++) {
      clearTimeout(this.state.playlist[i]);
    }
  }

  render() {

    return (
      <div>
        <Canvas lines={this.state.lines}/>
        <button onClick={() => this.showLines()}>show lines</button>
        <button onClick={() => this.clearLines()}>clear</button>
      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
