import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Line(props) {
  return (
    <line
      className="line"
      x1={props.x1.toString()}
      y1={props.y1.toString()}
      x2={props.x2.toString()}
      y2={props.y2.toString()}/>
  )
}

class Canvas extends React.Component {

  renderLine(i, x1, y1, x2, y2) {
    return (
      <Line
        key={i}
        x1={x1}
        y1={y1}
        x2={x2}
        y2={y2}
      />
    );
  }

  render() {
    return (
      <div>
        <svg height="500" width="500">
          {[
            this.renderLine(0, 0, 0, 200, 200),
            this.renderLine(1, 0, 0, 100, 200),
            this.renderLine(2, 0, 0, 5, 20),
            this.renderLine(3, 0, 0, 35, 160)
          ]}
        </svg>
      </div>
    );
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      lines: [{
        i: 0,
        x1: 0,
        x2: 0,
        y1: 100,
        y2: 100,
      }],
    }
  }

  render() {
    return (
      <Canvas />
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
