import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function Line(props) {
  return (
    <line
      className="line"
      x1={props.x1}
      y1={props.y1}
      x2={props.x2}
      y2={props.y2}/>
  )
}

class Canvas extends React.Component {

  renderLine(i, x1, y1, x2, y2) {
    return (
      <Line
        key={i}
        x1={x1}
        y1={y1}
        x2={x2}
        y2={y2}
      />
    );
  }

  renderLines = (lines) =>
      lines.map((line, i) => {
        return this.renderLine(line.i, line.x1, line.y1, line.x2, line.y2)
      }
    );

  render() {
    return (
      <div>
        <svg height="500" width="500">
          {this.renderLines(this.props.lines)}
        </svg>
      </div>
    );
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      lines: [{
        i: 0,
        x1: 0,
        x2: 0,
        y1: 100,
        y2: 100,
      }],
    }
  }

  render() {
    return (
      <Canvas lines={this.state.lines}/>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
